package com.abhiiscoding.taskharbor.activitiesimport android.annotation.SuppressLintimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.os.Handlerimport android.view.WindowManagerimport com.abhiiscoding.taskharbor.databinding.ActivitySplashBindingimport com.abhiiscoding.taskharbor.firebase.FirestoreClass@SuppressLint("CustomSplashScreen")class SplashActivity : AppCompatActivity() {    private var binding: ActivitySplashBinding? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding = ActivitySplashBinding.inflate(layoutInflater)        setContentView(binding?.root)        window.setFlags(            WindowManager.LayoutParams.FLAG_FULLSCREEN,            WindowManager.LayoutParams.FLAG_FULLSCREEN        )//        val typeface: Typeface =//            Typeface.createFromAsset(assets, "carbon bl.ttf")//        binding?.tvAppName?.typeface = typeface        Handler().postDelayed({            var currentUserId = FirestoreClass().getCurrentUserID()            if (currentUserId.isNotEmpty()) {                startActivity(Intent(this, MainActivity::class.java))            } else {                startActivity(Intent(this, IntroActivity::class.java))            }            // close this activity            finish()        }, 3000) // wait for 3 seconds    }}